// src/App.js
import React, { useEffect, useState } from 'react';
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
} from 'react-router-dom';

import AuthPage from './components/AuthPage';
import {
  getCurrentPositionAsync,
  fetchWeatherAsync,
} from './services/LocationWeatherService';

function HomePage() {
  const [weather, setWeather] = useState(null);
  const [status, setStatus] = useState('loading'); // 'loading' | 'error' | 'ready'
  const [error, setError] = useState('');

  useEffect(() => {
    (async () => {
      try {
        const coords = await getCurrentPositionAsync();
        const data = await fetchWeatherAsync(coords);
        setWeather({
          city: data.name,
          temp: Math.round(data.main.temp),
          desc: data.weather[0].description,
        });
        setStatus('ready');
      } catch (e) {
        setError(e.message);
        setStatus('error');
      }
    })();
  }, []);

  if (status === 'loading') {
    return <p className="centeredMessage">Loading weatherâ€¦</p>
  }
  if (status === 'error') {
    return (
      <p style={{ textAlign: 'center', marginTop: 40 }}>
        Could not load weather: {error}
      </p>
    );
  }

  return (
    <div style={{ textAlign: 'center', marginTop: 40 }}>
      <h2>Current Weather in {weather.city}</h2>
      <p style={{ fontSize: 48 }}>{weather.temp}Â°C</p>
      <p style={{ textTransform: 'capitalize' }}>{weather.desc}</p>
      {/* ðŸ‘‡ here, call your outfit-suggestion logic with weather.temp / weather.desc */}
    </div>
  );
}

function App() {
  return (
    <Router>
      <Routes>
        {/* Home / main app screen */}
        <Route path="/" element={<HomePage />} />

        {/* Auth screens */}
        <Route path="/login" element={<AuthPage mode="login" />} />
        <Route path="/signup" element={<AuthPage mode="signup" />} />

        {/* Redirect everything else to login */}
        <Route path="*" element={<Navigate to="/login" replace />} />
      </Routes>
    </Router>
  );
}

export default App;
