// web/src/App.js

import React, { useEffect, useState } from 'react';
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
} from 'react-router-dom';

import AuthPage from './components/AuthPage';
import {
  getCurrentPositionAsync,
  fetchWeatherAsync,
} from './services/LocationWeatherService';
import { getSuggestedOutfit } from './services/OutfitService';
import ProtectedRoute from './components/ProtectedRoute';

function HomePage() {
  const [weather, setWeather] = useState(null);
  const [status, setStatus] = useState('loading'); // 'loading' | 'error' | 'ready'
  const [error, setError] = useState('');

  useEffect(() => {
    (async () => {
      try {
        const coords = await getCurrentPositionAsync();
        const data = await fetchWeatherAsync(coords);
        setWeather({
          city: data.name,
          temp: Math.round(data.main.temp),
          desc: data.weather[0].description,
        });
        setStatus('ready');
      } catch (e) {
        setError(e.message);
        setStatus('error');
      }
    })();
  }, []);

  if (status === 'loading') {
    return (
      <p
        style={{
          textAlign: 'center',
          marginTop: 40,
        }}
      >
        Loading weather…
      </p>
    );
  }

  if (status === 'error') {
    return (
      <p
        style={{
          textAlign: 'center',
          marginTop: 40,
        }}
      >
        Could not load weather: {error}
      </p>
    );
  }

  // At this point, weather is ready
  const outfit = getSuggestedOutfit(weather.temp, weather.desc);

  return (
    <div style={{ textAlign: 'center', marginTop: 40 }}>
      <h2>Current Weather in {weather.city}</h2>
      <p style={{ fontSize: 48 }}>{weather.temp}°C</p>
      <p style={{ textTransform: 'capitalize' }}>{weather.desc}</p>

      <div style={{ marginTop: 24 }}>
        <h3>Suggested Outfit</h3>
        <p style={{ fontSize: 18 }}>{outfit}</p>
      </div>
    </div>
  );
}

export default function App() {
  return (
    <Router>
      <Routes>
        {/* only signed-in users get HomePage */}
        <Route
          path="/"
          element={
            <ProtectedRoute>
              <HomePage />
            </ProtectedRoute>
          }
        />

        {/* Auth screens */}
        <Route path="/login" element={<AuthPage mode="login" />} />
        <Route path="/signup" element={<AuthPage mode="signup" />} />

        {/* catch-all */}
        <Route path="*" element={<Navigate to="/login" replace />} />
      </Routes>
    </Router>
  );
}
